// File: /server/routes/reportings.js
const express = require("express");
const router = express.Router();
const Reporting = require("../models/Reporting");

// POST: Create new reporting
router.post("/", async (req, res) => {
  try {
    const newReport = new Reporting(req.body);
    const savedReport = await newReport.save();
    res.status(201).json(savedReport);
  } catch (err) {
    res.status(400).json({ error: err.message });
  }
});

// GET: Retrieve all reportings
router.get("/", async (req, res) => {
  try {
    const reports = await Reporting.find().sort({ createdAt: -1 });
    res.json(reports);
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// PUT: Update reporting by ID
router.put("/:id", async (req, res) => {
  try {
    const updatedReport = await Reporting.findByIdAndUpdate(
      req.params.id,
      req.body,
      { new: true }
    );
    res.json(updatedReport);
  } catch (err) {
    res.status(400).json({ error: err.message });
  }
});

// DELETE: Remove reporting by ID
router.delete("/:id", async (req, res) => {
  try {
    await Reporting.findByIdAndDelete(req.params.id);
    res.json({ message: "Reporting supprim√©" });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

module.exports = router;
